// Analyse et classement par OCR
const document = items[0].json;
const documentContent = document.content || "";

// Simulation d'une analyse OCR & IA
let category = "Divers";
let indicators = [];

if(documentContent.includes("convention") || document.type === "convention") {
  category = "Conventions";
  indicators = ["1.1", "1.2"];
} else if(documentContent.includes("attestation") || document.type === "attestation") {
  category = "Attestations";
  indicators = ["3.1", "3.2"];
} else if(documentContent.includes("programme") || document.type === "programme") {
  category = "Programmes";
  indicators = ["2.1"];
} else if(documentContent.includes("√©valuation") || document.type === "evaluation") {
  category = "√âvaluations";
  indicators = ["2.3", "7.2"];
} else if(documentContent.includes("formateur") || document.type === "cv") {
  category = "Formateurs";
  indicators = ["5.1", "5.2"];
}

return [
  {
    json: {
      ...document,
      category,
      relatedIndicators: indicators,
      classificationDate: new Date().toISOString(),
      isClassified: true
    }
  }
];

// V√©rifier les docs qui vont bient√¥t expirer (dans les 30 jours)
const documents = items;
const today = new Date();
const warningDocs = [];

for (const doc of documents) {
  if (doc.json.expirationDate) {
    const expDate = new Date(doc.json.expirationDate);
    const diffTime = expDate - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays <= 30 && diffDays > 0) {
      warningDocs.push({
        json: {
          ...doc.json,
          daysUntilExpiration: diffDays
        }
      });
    }
  }
}

return warningDocs;

// Analyse de conformit√©
const indicators = items[0].json.data; // tous les indicateurs
const documents = items[1].json.data; // tous les documents

const conformityReport = [];
let globalConformity = 0;

// Pour chaque indicateur, v√©rifier les documents associ√©s
for (const indicator of indicators) {
  const indicatorCode = indicator.fields.Code;
  const requiredDocs = indicator.fields.DocumentsRequis?.split(',').map(d => d.trim()) || [];

  // Compter combien de types requis sont couverts
  let coveredDocsCount = 0;
  let associatedDocs = [];

  for (const reqDoc of requiredDocs) {
    const matchingDocs = documents.filter(d => {
      return d.fields.type === reqDoc || 
             (d.fields.relatedIndicators && d.fields.relatedIndicators.includes(indicatorCode));
    });

    if (matchingDocs.length > 0) {
      coveredDocsCount++;
      associatedDocs = [...associatedDocs, ...matchingDocs.map(d => d.fields.reference)];
    }
  }

  // Calculer le taux de conformit√© pour cet indicateur
  const conformityRate = requiredDocs.length > 0 
    ? Math.round((coveredDocsCount / requiredDocs.length) * 100) 
    : 100;

  conformityReport.push({
    indicatorCode,
    indicatorName: indicator.fields.Libelle,
    conformityRate,
    requiredDocs,
    associatedDocs,
    status: conformityRate >= 100 ? 'conforme' : conformityRate >= 70 ? 'partiel' : 'non-conforme'
  });

  // Ajouter √† la conformit√© globale
  globalConformity += conformityRate;
}

// Moyenne de conformit√© globale
globalConformity = Math.round(globalConformity / indicators.length);

// Trier par priorit√© (non-conformes en premier)
conformityReport.sort((a, b) => a.conformityRate - b.conformityRate);

return [
  {
    json: {
      reportDate: new Date().toISOString(),
      globalConformity,
      globalStatus: globalConformity >= 90 ? 'conforme' : globalConformity >= 70 ? '√† am√©liorer' : 'critique',
      detailedReport: conformityReport
    }
  }
];

