// V√©rifier les certifications des formateurs qui expirent bient√¥t
const formateurs = items;
const today = new Date();
const alertFormateurs = [];

for (const formateur of formateurs) {
  const certifications = formateur.json.certifications || [];
  const alertCertifications = [];

  for (const cert of certifications) {
    if (cert.expirationDate) {
      const expDate = new Date(cert.expirationDate);
      const diffTime = expDate - today;
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

      if (diffDays <= 60 && diffDays > 0) {
        alertCertifications.push({
          ...cert,
          daysUntilExpiration: diffDays
        });
      }
    }
  }

  if (alertCertifications.length > 0) {
    alertFormateurs.push({
      json: {
        formateurId: formateur.json.id,
        nom: formateur.json.nom,
        prenom: formateur.json.prenom,
        email: formateur.json.email,
        certifications: alertCertifications
      }
    });
  }
}

return alertFormateurs;

