// Pr√©paration des donn√©es de formation pour g√©n√©ration des documents
const sessionData = items[0].json;

// Structurer les donn√©es pour les templates de documents
const formationData = {
  id: sessionData.id,
  titre: sessionData.titre,
  dateDebut: new Date(sessionData.dateDebut).toLocaleDateString('fr-FR'),
  dateFin: new Date(sessionData.dateFin).toLocaleDateString('fr-FR'),
  duree: sessionData.duree,
