  prerequis: sessionData.prerequis,
  modalitesEvaluation: sessionData.modalitesEvaluation,
  client: sessionData.client,
  dateGeneration: new Date().toLocaleDateString('fr-FR')
};

return [{ json: formationData }];

// Combinaison des r√©sultats des documents g√©n√©r√©s
const formationData = items[0].json;
const programmeDoc = items[1].json;
const conventionDoc = items[2].json;
const attestationDoc = items[3].json;

return [{
  json: {
    formationId: formationData.id,
    formationTitre: formationData.titre,
    dateGeneration: new Date().toISOString(),
    documents: {
      programme: programmeDoc.url || programmeDoc.fileContent,
      convention: conventionDoc.url || conventionDoc.fileContent,
      attestation: attestationDoc.url || attestationDoc.fileContent,
    },
    participants: formationData.participants,
    formateur: formationData.formateur,
    client: formationData.client
  }
}];

// Extraction des donn√©es de formation compl√©t√©e
const formationData = items[0].json;
const participants = formationData.participants || [];

// Cr√©er un √©l√©ment par participant pour les √©valuations
const participantsItems = [];

for (const participant of participants) {
  participantsItems.push({
    json: {
      formationId: formationData.id,
      formationTitre: formationData.titre,
      dateFin: formationData.dateFin,
      participant: participant,
      formateur: formationData.formateur,
      evaluationLink: `https://forms.example.com/evaluation?session=${formationData.id}&participant=${participant.id}`
    }
  });
}

return participantsItems;

// Traitement de l'√©valuation re√ßue
const evaluationData = items[0].json;

// Calculer un score global
const scores = evaluationData.scores || {};
let totalScore = 0;
let count = 0;

for (const key in scores) {
  if (typeof scores[key] === 'number') {
    totalScore += scores[key];
    count++;
  }
}

const averageScore = count > 0 ? Math.round((totalScore / count) * 10) / 10 : 0;

// Identifier les points d'am√©lioration
const lowScoreThreshold = 3;
const improvementAreas = [];

for (const key in scores) {
  if (typeof scores[key] === 'number' && scores[key] <= lowScoreThreshold) {
    improvementAreas.push(key);
  }
}

// Pr√©parer le r√©sum√©
return [{
  json: {
    ...evaluationData,
    averageScore,
    improvementAreas,
    status: averageScore >= 4 ? 'satisfaisant' : averageScore >= 3 ? 'moyen' : 'insatisfaisant',
    dateReception: new Date().toISOString(),
    commentaireAnalyse: improvementAreas.length > 0 
      ? `Points √† am√©liorer : ${improvementAreas.join(', ')}` 
      : 'Aucun point critique identifi√©'
  }
}];

