// G√©n√©rer le rapport mensuel d'√©valuation
const evaluations = items[0].json.data;
const now = new Date();
const monthAgo = new Date();
monthAgo.setMonth(now.getMonth() - 1);

// Filtrer les √©valuations du dernier mois
const recentEvaluations = evaluations.filter(eval => {
  const evalDate = new Date(eval.fields.dateReception);
  return evalDate >= monthAgo && evalDate <= now;
});

// Calculer les statistiques
let totalScore = 0;
const formationScores = {};
const formateurScores = {};
const improvementAreasCount = {};

recentEvaluations.forEach(eval => {
  // Score moyen global
  totalScore += eval.fields.averageScore || 0;

  // Scores par formation
  const formationId = eval.fields.formationId;
  if (!formationScores[formationId]) {
    formationScores[formationId] = {
      titre: eval.fields.formationTitre,
      scores: [],
      total: 0,
      count: 0
    };
  }
  formationScores[formationId].scores.push(eval.fields.averageScore);
  formationScores[formationId].total += eval.fields.averageScore;
  formationScores[formationId].count++;

  // Scores par formateur
  const formateurId = eval.fields.formateurId;
  if (!formateurScores[formateurId]) {
    formateurScores[formateurId] = {
      nom: eval.fields.formateurNom,
      scores: [],
      total: 0,
      count: 0
    };
  }
  formateurScores[formateurId].scores.push(eval.fields.averageScore);
  formateurScores[formateurId].total += eval.fields.averageScore;
  formateurScores[formateurId].count++;

  // Points d'am√©lioration
  (eval.fields.improvementAreas || []).forEach(area => {
    if (!improvementAreasCount[area]) {
      improvementAreasCount[area] = 0;
    }
    improvementAreasCount[area]++;
  });
});

// Calcul des moyennes
const globalAverage = recentEvaluations.length > 0 ? Math.round((totalScore / recentEvaluations.length) * 10) / 10 : 0;

// Classement des formations par score
const formationRanking = Object.keys(formationScores)
  .map(id => ({
    id,
    titre: formationScores[id].titre,
    average: formationScores[id].count > 0 ? Math.round((formationScores[id].total / formationScores[id].count) * 10) / 10 : 0
  }))
  .sort((a, b) => b.average - a.average);

// Classement des formateurs par score
const formateurRanking = Object.keys(formateurScores)
  .map(id => ({
    id,
    nom: formateurScores[id].nom,
    average: formateurScores[id].count > 0 ? Math.round((formateurScores[id].total / formateurScores[id].count) * 10) / 10 : 0
  }))
  .sort((a, b) => b.average - a.average);

// Points d'am√©lioration les plus fr√©quents
const topImprovementAreas = Object.keys(improvementAreasCount)
  .map(area => ({ area, count: improvementAreasCount[area] }))
  .sort((a, b) => b.count - a.count)
  .slice(0, 5);

// Rapport final
return [{
  json: {
    periode: `${monthAgo.toLocaleDateString()} - ${now.toLocaleDateString()}`,
    dateGeneration: now.toISOString(),
    nombreEvaluations: recentEvaluations.length,
    scoreMoyenGlobal: globalAverage,
    tendance: 0, // √Ä calculer avec l'historique
    meilleuresFormations: formationRanking.slice(0, 3),
    formationsAmeliorer: formationRanking.slice(-3).reverse(),
    meilleursFormateurs: formateurRanking.slice(0, 3),
    pointsAmelioration: topImprovementAreas,
    qualiopiStatus: globalAverage >= 4 ? 'conforme' : globalAverage >= 3.5 ? '√† surveiller' : 'non-conforme'
  }
}];
